const messagesArray = $('Busca as mensagens').all();
let allMessages = [];

// Iterar sobre as mensagens e concatenar
messagesArray.forEach(item => {
  try {
    // Parse cada mensagem como JSON
    let parsedMessage = JSON.parse(item.json.message);

    // Adicionar cada mensagem ao array de mensagens
    if (Array.isArray(parsedMessage)) {
      allMessages = allMessages.concat(parsedMessage);
    } else {
      allMessages.push(parsedMessage);
    }
  } catch (error) {
    console.error("Erro ao parsear a mensagem:", error);
  }
});

// Retornar o array de mensagens diretamente como um JSON estruturado
return [{"messages": allMessages}];

const messagesArray = $('Busca as mensagens').all();
let allMessages = [];

// Função para verificar se uma string é um JSON válido
function isValidJSON(str) {
  try {
    JSON.parse(str);
    return true;
  } catch (error) {
    return false;
  }
}

// Iterar sobre as mensagens e concatenar
messagesArray.forEach(item => {
  let messageContent = item.json.message;

  // Se for JSON válido, parse como JSON
  if (isValidJSON(messageContent)) {
    let parsedMessage = JSON.parse(messageContent);

    // Adicionar a mensagem ao array (pode ser um array ou objeto)
    if (Array.isArray(parsedMessage)) {
      allMessages = allMessages.concat(parsedMessage);
    } else {
      allMessages.push(parsedMessage);
    }
  } else {
    // Se não for JSON, apenas adicionar a string diretamente
    allMessages.push(messageContent);
  }
});

// Retornar o array de mensagens diretamente como um JSON estruturado
return [{"messages": allMessages}];
