// Pega os dados do node "Lista dados t√≠tulos"
const titulos = $node["Lista dados t√≠tulos"].json.resultado;

// Pega os dados do node "Junta pagamentos"
const pagamentos = $json.data;

// Cria a lista de objetos com os dados combinados, associando pela propriedade nufin
const Titulos = titulos.map(titulo => {
  // Procura o pagamento que tenha o mesmo nufin do t√≠tulo
  const pagamentoEncontrado = pagamentos.find(p => p.nufin === titulo.nufin);

  return {
    nufin: titulo.nufin,
    numero_contrato: titulo.contrato,
    data_vencimento: titulo.data_vencimento,
    dias_vencido: titulo.dias_vencido,
    // Se encontrado, insere o objeto pagamento com method e chave; caso contr√°rio, null
    pagamento: pagamentoEncontrado ? pagamentoEncontrado.pagamento : null
  };
});

// Retorna o resultado em uma lista √∫nica
return [{ json: { Titulos } }];

const item = $input.last();

const nufin_array = item.json.nufins.split(',').map(n => n.trim());
const contrato_array = item.json.contratos.split(',').map(c => c.trim().split(' ')[0]); // Remove par√™nteses e dias vencidos

const resultado = [];

// Fun√ß√£o para calcular a data de vencimento
function calcula_data_vencimento(diasVencidos) {
  const dataAtual = new Date();
  dataAtual.setDate(dataAtual.getDate() - diasVencidos); // Subtrai dias (negativos somam dias futuros)
  const dia = String(dataAtual.getDate()).padStart(2, '0');
  const mes = String(dataAtual.getMonth() + 1).padStart(2, '0'); 
  const ano = dataAtual.getFullYear();
  return `${dia}/${mes}/${ano}`;
}

// Construindo a lista de objetos com nufin, contrato e data de vencimento
for (let i = 0; i < nufin_array.length; i++) {
  const diasVencidosMatch = item.json.contratos.split(',')[i].match(/\(([-\d]+) dias vencidos\)/);
  const diasVencidos = parseInt(diasVencidosMatch?.[1] || "0", 10); // Suporte para dias negativos

  const vencimento = calcula_data_vencimento(diasVencidos);

  resultado.push({
    nufin: nufin_array[i],
    contrato: contrato_array[i],
    data_vencimento: vencimento,
    dias_vencido: diasVencidos,
  });
}

// Ordena o array resultado pela data_vencimento (formato dd/mm/yyyy)
resultado.sort((a, b) => {
  const [diaA, mesA, anoA] = a.data_vencimento.split('/');
  const [diaB, mesB, anoB] = b.data_vencimento.split('/');
  const dataA = new Date(anoA, mesA - 1, diaA);
  const dataB = new Date(anoB, mesB - 1, diaB);
  return dataA - dataB;
});

return [{ json: { resultado, numeroContratosUnicos: new Set(contrato_array).size } }];
