function extrairConteudoEReferencias(html) {
    // Remove scripts, styles, header, footer, navegação visual, forms, menus conhecidos (mantendo <a href>, <img src>, <video src>)
    html = html
      .replace(/<style[\s\S]*?<\/style>/gi, '')
      .replace(/<script[\s\S]*?<\/script>/gi, '')
      .replace(/<footer[\s\S]*?<\/footer>/gi, '')
      .replace(/<header[\s\S]*?<\/header>/gi, '')
      .replace(/<nav[\s\S]*?<\/nav>/gi, '')
      .replace(/<form[\s\S]*?<\/form>/gi, '');

    // Remove apenas DIVs de menu, rodapé, logo, etc, mas **MANTÉM links e imagens!**
    html = html.replace(/<div[^>]+(menu|nav|logo|footer|rodape|sidebar)[^>]*>[\s\S]*?<\/div>/gi, '');

    // Extração de partes relevantes:
    let saida = [];

    // 1. Títulos e textos (h1-h4, p, li)
    let titulos = html.match(/<(h[1-4])[^>]*>(.*?)<\/\1>/gi);
    if (titulos) titulos.forEach(h => {
        let t = h.replace(/<[^>]+>/g,'').trim();
        if(t) saida.push("TITULO: " + t);
    });

    let paragrafos = html.match(/<p[^>]*>([\s\S]*?)<\/p>/gi);
    if (paragrafos) paragrafos.forEach(p => {
        let t = p.replace(/<[^>]+>/g,'').trim();
        if(t) saida.push(t);
    });

    // 2. Imagens relevantes
    let imagens = [];
    let regexImg = /<img[^>]+src\s*=\s*["'](.*?)["'][^>]*>/gi;
    let imgMatch;
    while (imgMatch = regexImg.exec(html)) {
        if (!/logo|favicon|icon/i.test(imgMatch[1])) { // se quiser todas, remova este if!
            imagens.push(imgMatch[1]);
        }
    }
    if(imagens.length) {
        saida.push('IMAGENS:\n' + imagens.join('\n'));
    }

    // 3. TODOS os links relevantes para download, referências e conteúdo (PDF, XLS, Google Drive, ficha, imagens, vídeos, etc)
    let links = [];
    let regexA = /<a[^>]+href\s*=\s*["'](.*?)["'][^>]*>([\s\S]*?)<\/a>/gi;
    let aMatch;
    while (aMatch = regexA.exec(html)) {
        let href = aMatch[1];
        // Só filtra lixos internos (anchor) e navegação (não é obrigatório, mas pode ser útil, ajuste como preferir)
        if (
            !href.startsWith('#') && 
            !href.startsWith('javascript:') && 
            !href.match(/(?:mailto:|tel:)/i)
        ) {
            links.push(`${href} [${aMatch[2].replace(/<[^>]+>/g,'').trim()}]`);
        }
    }
    if (links.length) {
        saida.push('LINKS/REFERÊNCIAS:\n' + links.join('\n'));
    }

    // 4. Coleta FAQs (accordion perguntas e respostas)
    let faqs = [];
    let regexFAQ = /<div[^>]+class=["'][^"']*elementor-accordion-item[^"']*["'][^>]*>([\s\S]*?)<\/div>/gi;
    let faqMatch;
    while(faqMatch = regexFAQ.exec(html)) {
        // pega pergunta
        let per = faqMatch[1].match(/class=["'][^"']*tab-title[^"']*["'][^>]*>([\s\S]*?)<\/([a-z]+|div)>/i);
        let res = faqMatch[1].match(/class=["'][^"']*tab-content[^"']*["'][^>]*>([\s\S]*?)<\/div>/i);
        if(per && res) {
            per = per[1].replace(/<[^>]+>/g,'').trim();
            res = res[1].replace(/<[^>]+>/g,'').trim();
            if(per && res) faqs.push("Q: " + per + "\nA: " + res);
        }
    }
    if(faqs.length) saida.push("FAQ:\n" + faqs.join('\n\n'));

    // 5. Vídeos
    let videos = [];
    let regexVideo = /<video[^>]+src\s*=\s*["']([^"']+)["'][^>]*>/gi;
    let videoMatch;
    while(videoMatch = regexVideo.exec(html)) {
        videos.push(videoMatch[1]);
    }
    if(videos.length) saida.push("VIDEOS:\n"+videos.join('\n'));

    return saida.join('\n\n');
}


